mapper.select:
SELECT movie_name FROM tb_movie


        movie_country=#{movieCountry}


        AND movie_date=#{movieDate}


        AND movie_type=#{movieType}

mapper.update:
UPDATE tb_movie
movie_name=#{movieName}, movie_director=#{movieDirector}, movie_star=#{movieStar}, movie_type=#{movieType}, movie_country=#{movieCountry}, movie_language=#{movieLanguage}, movie_time=#{movieTime}, movie_detail=#{movieDetails}, movie_date=#{movieDate}, movie_score=#{movieScore}, movie_img=#{movieImg}, movie_preparation=#{moviePreparation},
WHERE movie_id=#{movieId}
